import { useEffect, useState } from 'react'

export default () => {
  const [active, setActive] = useState<string>('start')
  const [score, setScore] = useState<number>(0)
  const [status, setStatus] = useState<string>()
  const [player, setPlayer] = useState<number>(0)
  const [computer, setComputer] = useState<number>(0)

  const handleResult = (computer: number) => {
    if (computer === player) {
      setStatus('DRAW')
      return 'empate'
    }
    if ((computer === 0 && player === 2) || (computer === 1 && player === 0) || (computer === 2 && player === 1)) {
      setStatus('YOU LOOSE')
      setScore(prev => prev - 1)
      return 'computer wins'
    }
    setStatus('YOU WIN')
    setScore(prev => prev + 1)
    return 'player wins'
  }

  useEffect(() => {
    if (active === 'loading')
      setTimeout(() => {
        const jogada = Math.floor(Math.random() * 3)
        console.log(jogada)
        setActive('fim')
        setComputer(jogada)
        handleResult(jogada)
      }, 1000)
  }, [active])

  const play = (move: number) => {
    setActive('loading')
    setPlayer(move)
  }

  const playAgain = (move: number) => {
    setActive('start')
  }

  return { play, score, status, computer, player, playAgain }
}
