import React, { useEffect, useState } from 'react'
import ButtonControl from '../components/ButtonControl'
import scissors from '../assets/icon-scissors.svg'
import rock from '../assets/icon-rock.svg'
import paper from '../assets/icon-paper.svg'
import * as Style from './styles'
import Score from '../components/Score'
import Button from '../components/Button'

const Jokenpo = () => {
  const [active, setActive] = useState('start')
  const [score, setScore] = useState<number>(0)
  const [status, setStatus] = useState<string>()
  const [player, setPlayer] = useState<number>(0)

  const play = (computer: number) => {
    if (computer === player) {
      setStatus('Empate')
      return 'empate'
    }
    if (computer >= player % 2) {
      setStatus('YOU LOOSE')
      setScore(prev => prev - 1)
      return 'computer wins'
    } else {
      setStatus('YOU WIN')
      setScore(prev => prev + 1)
      return 'player wins'
    }
  }

  useEffect(() => {
    if (active === 'loading')
      setTimeout(() => {
        setActive('fim')
        play(Math.floor(Math.random() * 3))
      }, 3000)
  }, [active])

  const handleAction = (active: string, cart: number) => {
    setActive(active)
    setPlayer(cart)
  }

  return (
    <Style.Container>
      <Style.WrapperControl>
        <div className={'score'}>
          <Score score={score} />
        </div>
        {active === 'start' && (
          <>
            <div className={'content'}>
              <ButtonControl img={rock} color="green" onClick={() => handleAction('loading', 0)} />
              <ButtonControl img={paper} color="yellow" onClick={() => handleAction('loading', 1)} />
            </div>
            <div>
              <ButtonControl img={scissors} color="cyan" onClick={() => handleAction('loading', 2)} />
            </div>
          </>
        )}
        {active === 'loading' && (
          <>
            <div className={'content'}>
              <ButtonControl img={rock} color="green" />
              <ButtonControl color="red" />
            </div>
            <div className={'loading'}>
              <p>MACHINE IS CHOOSING...</p>
            </div>
          </>
        )}
        {active === 'fim' && (
          <>
            <div className={'content'}>
              <ButtonControl img={rock} color="green" />
              <ButtonControl img={scissors} color="cyan" />
            </div>
            <div className={'loading'}>
              <p>{status}</p>
            </div>
            <Button onClick={() => setActive('start')}>PLAY AGAIN</Button>
          </>
        )}
      </Style.WrapperControl>
    </Style.Container>
  )
}

export default Jokenpo
