import React, { useState } from 'react'
import ButtonControl from '../components/ButtonControl'
import scissors from '../assets/icon-scissors.svg'
import rock from '../assets/icon-rock.svg'
import paper from '../assets/icon-paper.svg'
import * as Style from './styles'
import Score from '../components/Score'
import Button from '../components/Button'

const Jokenpo = () => {
  const [active, setActive] = useState('start')
  const [score, setScore] = useState<number>(0)
  const [status, setStatus] = useState<string>()
  const [computer, setComputer] = useState<number>(0)
  const [player, setPlayer] = useState<number>(0)

  const play = () => {
    if (computer === player) {
      setStatus('Empate')
      return 'empate'
    }
    if (computer >= player % 2) {
      setStatus('YOU LOOSE')
      return 'computer wins'
    } else {
      setStatus('YOU WIN')
      return 'player wins'
    }
  }

  const handleAction = (active: string, cart: number) => {
    setActive(active)
  }

  return (
    <Style.Container>
      <Style.WrapperControl>
        <div className={'score'}>
          <Score />
        </div>
        {active === 'start' && (
          <>
            <div className={'content'}>
              <ButtonControl img={rock} color="green" onClick={() => setActive('loading')} />
              <ButtonControl img={paper} color="yellow" />
            </div>
            <div>
              <ButtonControl img={scissors} color="cyan" />
            </div>
          </>
        )}
        {active === 'loading' && (
          <>
            <div className={'content'}>
              <ButtonControl img={rock} color="green" onClick={() => setActive('fim')} />
              <ButtonControl color="red" />
            </div>
            <div className={'loading'}>
              <p>MACHINE IS CHOOSING...</p>
            </div>
          </>
        )}
        {active === 'fim' && (
          <>
            <div className={'content'}>
              <ButtonControl img={rock} color="green" onClick={() => setActive('start')} />
              <ButtonControl img={scissors} color="cyan" />
            </div>
            <div className={'loading'}>
              <p>YOU LOOSE</p>
            </div>
            <Button>PLAY AGAIN</Button>
          </>
        )}
      </Style.WrapperControl>
    </Style.Container>
  )
}

export default Jokenpo
